import React, {Fragment} from "react";
import {Button} from 'react-bootstrap';  //npm install react-bootstrap bootstrap@5.1.3
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';

const Counter = () => {

	const [counter, setCounter] = React.useState(0);
	
	return(
    <Fragment>
	  <div className="text-center" >
	   <h1>{counter}</h1>
	   <div>
	    <Button variant="primary" id="button" onClick={ () => setCounter(counter + 1)}>
            Increment +</Button>
	   </div>
	  </div>
    </Fragment>
	);
}
export default Counter;
