import React,{Component, Fragment} from 'react';
import { Container } from 'react-bootstrap';
import Navberr from "../components/home_component/Nav";
import Futer from '../components/home_component/Footer';
import '../asset/css/terms.css';
class TermsAndCondition extends Component{  
    render(){
        return (
        <Fragment>
            <Navberr />
            
            <div className="terms-box">
            <Container>
            <div className="terms-text">
            <h2>Terms Of Service Page</h2>
            <p>ওয়েবসাইট ডিজাইন অথবা ওয়েবসাইট ডেভেলপমেন্ট শেখার জন্য কিছু নিয়ম কানুন আছে<br></br>
               There are some rules for learning website design or website development.</p>
            <p>এই কাজটা অনেক কষ্টের এবং অনেক ধৈর্য্য ধরে কাজটা শিখতে হয় একটু লং টাইম নিয়ে কাজটা শিখলে ভালভাবে শেখা যায় এবং নিজের ক্যারিয়ারে ভালো হয়<br></br>
               It takes a lot of hard work and a lot of patience to learn the job.</p>
            <p>এই চাকরিতে বিশেষজ্ঞ হওয়ার জন্য প্রচুর অনুশীলন না করলে এই কাজ শেখা সম্ভব নয়<br></br>
               It is not possible to learn this job if you do not have to practice a lot to become an expert in this job.</p>
            <p></p>
            <p></p>
            <p></p>
            <p></p>
            <p></p>
            <h4>I Agree to the Terms of Service and I read the Privacy.</h4>
            <div className="buttons">
            <button className="btn red-btn">Accept</button>
            <button className="btn gray-btn">No Accept</button>
           </div>
            </div>
            
        </Container>
    </div>
            
            <Futer />
        </Fragment>
        )
    }
}
export default TermsAndCondition;
