import React,{Component} from 'react';
import axios from 'axios';


class Axioss extends Component{
    constructor(){
        super();
        this.state = {
            postData: " ",
            postReselt: " "
        }
    }
    onChangHandler = (event) => {
        var myData = event.target.value;
        this.setState({postData: myData})
    }
    onClickHandler = () => {
        axios.post('http://ss.rabbil.com/test.php', this.state.postData)
        .then(response =>{
             this.setState({postReselt: response.data})
             alert(this.state.postReselt);
        })
        .catch(error =>{
            alert("Soumthing Rong!");
        })
    } 
    render(){
       
        return <div>
            <p>{this.state.postReselt}</p>
                   <input onChange = {this.onChangHandler} type="text"/>
                   <button onClick = {this.onClickHandler}>Send</button>
               </div>
    }
}
export default Axioss;
