import React, {Component} from 'react';
//import ReactDOM from 'react-dom';

//import ReactDOM from 'react-dom';

class signUp extends Component{ 
    constructor(){
        super()
        this.state={
            fname: " ",
            lname: " ",
            email: " ",
            mibile: " "
        }
    }
                                          //14.form onSubmit handler Three
       
        render(){
            return (
            <div>
                <form>       
                    <input name="fname" type="text" placeholder='first name'/><br />
                    <input name="lname" type="text" placeholder='last name'/><br />
                    <input name="email" type="email" placeholder='your email'/><br />
                    <input name="mobile" type="text" placeholder='your mobile'/><br />
                    <input name="submit" type="submit" value="Save Now"/>
                </form>
            </div>
            );
    }
    }
        export default signUp;





