class PracticeReact extends Component{
    
    render(){
       const contry = [
           {
              city: 'Dhaka',
              zip: '1200'
           },
           {
            city: 'Brhammanbaria',
            zip: '1300'
           },
           {
            city: 'Selet',
            zip: '1100'
           }
       ];
       const contryItem = contry.map((axtra) =>{
          return <option>{'Is : ' + axtra}</option>})
        return(
            <Fragment>
                <div style={{textAlign: 'center', marginTop: '200px'}}>
                   <select>
                       {contryItem}
                   </select>
                </div>
            </Fragment>
        )}}
