import React, {Component} from 'react';
//import ReactDOM from 'react-dom';

class List extends Component{    //18.list (video 34 rabbil)
    constructor(){
    super();
    this.state={
    city1: "Dhaka",
    city2: "Rungpor",
    city3: "Bharmmanbarea",
    city4: "Kommilla",
    show: " ",
    auto: "Dhaka"
    } }
    onChangeHandler =(event)=>{
                    var seletedValue = event.target.value;
                    this.setState({show: seletedValue, auto: seletedValue})
                 }
    render(){
    return (
    <div>
        <p>{this.state.show}</p>
      <select onChange={this.onChangeHandler} value={this.state.auto}>
          <option>{this.state.city1}</option>
          <option>{this.state.city2}</option>
          <option>{this.state.city3}</option>
          <option>{this.state.city4}</option>
      </select>
    
    </div>
    );}}
    export default List;
    


