import React,{Component} from 'react'
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';

class RefressCom extends Component{
    constructor(){
        super();
        this.RefreshNow = this.RefreshNow.bind(this);
    }
    RefreshNow(){
        this.forceUpdate();        //components refresh
    }                                                
    render(){
        return (
            <div>
                <button onClick={this.RefreshNow} className="btn btn-primary m-5">
                    Refresh Now</button>
                <h2>{Math.random()}</h2>             
            </div>
        )                                 // rendom number genarete
    }
}
export default RefressCom;
