import React from "react";

const Increment = () => {
	const [counter, setCounter] = React.useState(0);
	return(
	  <div>
	   <h1 id="desplay">{counter}</h1>
	   <div>
	    <button id="button" onClick={ () => setCounter(counter + 1)}>Increment +</button>
	   </div>
	  </div>
	);
}
export default Increment;
