import React, {Component} from 'react';
//import ReactDOM from 'react-dom';

class SignUp extends Component{ 
    constructor(){
        super()
        this.state={
            fname: "",
            lname: "",
            email: "",
            mibile: ""
        } }
    onChangeHandler=(event)=>{
              var inputName = event.target.name;
              var inputValue = event.target.value;
              this.setState({[inputName]: inputValue});

              if(inputName === "fname"){
                   var namePattern = /^([a-zA-Z]){2,30}$/;
                   if(!namePattern.test(inputValue)){
                   this.setState({fname: "First Name Is  Not Valit"});
                   }
              }
              if(inputName === "lname"){
                var namePattern = /^([a-zA-Z]){2,30}$/;
                if(!namePattern.test(inputValue)){
                this.setState({lname: "last Name Is  Not Valit"});
                  }
             }
              if(inputName === "email"){
                var emailPattern = /\S+@\S+\.\S+/;
                if(!emailPattern.test(inputValue)){
                this.setState({email: "Email Is  Not Valit"});
                  }
              }
              if(inputName === "mobile"){
                if(!Number(inputValue)){
                this.setState({mobile: "mobile number Is  Not Valit"});
                  }
            }           
        }                                       //15.form Validation five  
        render(){
            return (
            <div>
                <p>First name: {this.state.fname}</p>
                <p>lname name: {this.state.lname}</p>
                <p>email: {this.state.email}</p>
                <p>mobile: {this.state.mobile}</p>
                <form>       
                    <input onChange={this.onChangeHandler} name="fname" type="text" placeholder='first name'/><br />
                    <input onChange={this.onChangeHandler} name="lname" type="text" placeholder='last name'/><br />
                    <input onChange={this.onChangeHandler} name="email" type="email" placeholder='your email'/><br />
                    <input onChange={this.onChangeHandler} name="mobile" type="text" placeholder='your mobile'/><br />
                    <input onChange={this.onChangeHandler} name="submit" type="submit" value="Save Now"/>
                </form>
            </div>
            );
    }}
        export default SignUp;





