import React,{Component} from 'react';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';


class Sstate extends Component{
    constructor(){
                super()
                this.state = {
                    name: 'Ismail'
                }
            }

            changeName(a){
               this.setState({name: a})
            }
    render(){
        return <div>
            <h3>{this.state.name} </h3>
            <button onClick={this.changeName.bind(this, 'ismail saheb')}>Change Name</button>
        </div>
    }
}
export default Sstate;


