import React, { Component, Fragment } from 'react';

const ObjactTry = {
    name: 'Ismail Saheb',
    age: ['10', '20', '30', '33'],
    phone: '01874631310'
 }
class PracticeReact extends Component{
    constructor(){
        super()
        this.state = ObjactTry;
    }
    render(){
        return(
            <Fragment>
                <div>
                    <h1>{this.state.name}</h1>
                    <h2>{this.state.age}</h2>
                    <h3>{this.state.phone}</h3>
                </div>
            </Fragment>
        )
    }
}
export default PracticeReact;


